
public class switchcasechar15 {

	public static void main(String[] args) {
		char d = 'l';
		switch(d)
		{
		case 'x':
		case 'X':
			System.out.println("The value of l is x");
			break;
		case 'y':
		case 'Y':
			System.out.println("The value of l is y");
			break;
		case 'z':
		case 'Z':
			System.out.println("The value of l is z");
			break;
			default:
				System.out.println("The value of l is other than x,y,z");
				break;



		}
		// TODO Auto-generated method stub

	}

}
